// box!
var box           = blessed.box({
  top: '5%',
  left: 'center',
  width: '90%',
  height: '85%',
  content: slides[slide_count],
  tags: true,
  border: {
    type: 'line'
  },
  style: {
    fg: 'white',
    border: {
      fg: '#00ff99'
    }
  }
});
// progress bar!
var progress_bar  = blessed.progressbar({
  width: '90%',
  top: '90%',
  left: 'center',
  height: '8%',
  orientation: 'horizontal',
  border: {
    type: 'line',
    fg: 'magenta'
  },
  filled: Math.round(( 1 / (slides.length-1)) * 100),
  pch: ''
});

screen.title = 'my slides r #blessed';

// box you go here *puts down box*
screen.append(box);
screen.append(progress_bar);

progress_bar.on('complete', function() {
  progress_bar['pch'] = '';
});

// left right keypress handlers
screen.key(['right'], function(ch, key) {
  if (slide_count < (slides.length-1)) slide_count += 1;
  box.setContent(slides[slide_count]);
  progress_bar.setProgress(Math.round(( slide_count / (slides.length-1)) * 100));
  screen.render();
});

screen.key(['left'], function(ch, key) {
  progress_bar['pch'] = '';
  if (slide_count > 1) slide_count -= 1;
  box.setContent(slides[slide_count]);
  progress_bar.setProgress(Math.round(( slide_count / (slides.length-1)) * 100));
  screen.render();
});

// g'bye: Escape, q, or Control-C
screen.key(['escape', 'q', 'C-c'], function(ch, key) {
  return process.exit(0);
});

// focus our element
box.focus();

// ta-da!
screen.render();
